var utilities = require('./utilities.js');

var counter = (function() {

	var _count = 0;
	var _pi = 3.14; 
	return {
		increment: function(){ 
			_count++;
			return _count;
		},
		reset : function(){
			_count = 0;
		},
		show : function(){
			return _count;
		},
		pi: _pi
		}

	
})();



// for (property in o) -> se obtienen todas las propiedades enumerables de un objeto y su cadena de prototipo
// Object.getOwnPropertyNames(o) -> Devuelve una matriz que contiene todos los nombres (enumerables o no) de las propiedades de un objeto

var properties = Object.keys(counter);
var i;
for ( i in properties) {

	utilities.definePropertyAsNotWritable(counter, properties[i]);
}

module.exports = counter;


